-- Rosenberg Self-Esteem Scale (RSES)
INSERT INTO surveys (title, description, version, source, schema) VALUES (
  'Rosenberg Self-Esteem Scale (RSES)',
  'The Rosenberg Self-Esteem Scale is a 10-item Likert scale with items answered on a four-point scale - from strongly agree to strongly disagree. It measures global self-worth by measuring both positive and negative feelings about the self.',
  '1.0',
  'Rosenberg, M. (1965). Society and the adolescent self-image. Princeton, NJ: Princeton University Press. Public Domain.',
  '{"questions":[{"id":"q1","text":"On the whole, I am satisfied with myself","type":"likert","options":[{"value":3,"label":"Strongly agree"},{"value":2,"label":"Agree"},{"value":1,"label":"Disagree"},{"value":0,"label":"Strongly disagree"}],"reverse_score":false},{"id":"q2","text":"At times I think I am no good at all","type":"likert","options":[{"value":3,"label":"Strongly agree"},{"value":2,"label":"Agree"},{"value":1,"label":"Disagree"},{"value":0,"label":"Strongly disagree"}],"reverse_score":true},{"id":"q3","text":"I feel that I have a number of good qualities","type":"likert","options":[{"value":3,"label":"Strongly agree"},{"value":2,"label":"Agree"},{"value":1,"label":"Disagree"},{"value":0,"label":"Strongly disagree"}],"reverse_score":false},{"id":"q4","text":"I am able to do things as well as most other people","type":"likert","options":[{"value":3,"label":"Strongly agree"},{"value":2,"label":"Agree"},{"value":1,"label":"Disagree"},{"value":0,"label":"Strongly disagree"}],"reverse_score":false},{"id":"q5","text":"I feel I do not have much to be proud of","type":"likert","options":[{"value":3,"label":"Strongly agree"},{"value":2,"label":"Agree"},{"value":1,"label":"Disagree"},{"value":0,"label":"Strongly disagree"}],"reverse_score":true},{"id":"q6","text":"I certainly feel useless at times","type":"likert","options":[{"value":3,"label":"Strongly agree"},{"value":2,"label":"Agree"},{"value":1,"label":"Disagree"},{"value":0,"label":"Strongly disagree"}],"reverse_score":true},{"id":"q7","text":"I feel that I''m a person of worth, at least on an equal plane with others","type":"likert","options":[{"value":3,"label":"Strongly agree"},{"value":2,"label":"Agree"},{"value":1,"label":"Disagree"},{"value":0,"label":"Strongly disagree"}],"reverse_score":false},{"id":"q8","text":"I wish I could have more respect for myself","type":"likert","options":[{"value":3,"label":"Strongly agree"},{"value":2,"label":"Agree"},{"value":1,"label":"Disagree"},{"value":0,"label":"Strongly disagree"}],"reverse_score":true},{"id":"q9","text":"All in all, I am inclined to feel that I am a failure","type":"likert","options":[{"value":3,"label":"Strongly agree"},{"value":2,"label":"Agree"},{"value":1,"label":"Disagree"},{"value":0,"label":"Strongly disagree"}],"reverse_score":true},{"id":"q10","text":"I take a positive attitude toward myself","type":"likert","options":[{"value":3,"label":"Strongly agree"},{"value":2,"label":"Agree"},{"value":1,"label":"Disagree"},{"value":0,"label":"Strongly disagree"}],"reverse_score":false}],"scoring_rules":{"total_score":{"type":"sum","questions":["q1","q2","q3","q4","q5","q6","q7","q8","q9","q10"],"description":"Total self-esteem score (0-30)"},"self_esteem_level":{"type":"threshold","input":"total_score","thresholds":[{"min":0,"max":15,"label":"Low self-esteem"},{"min":16,"max":25,"label":"Normal self-esteem"},{"min":26,"max":30,"label":"High self-esteem"}],"description":"Self-esteem level category"},"low_self_esteem_flag":{"type":"flag","condition":"total_score <= 15","message":"Low self-esteem detected - may benefit from support"}},"metadata":{"category":"Personality","license":"Public Domain","validated":true,"population":"Adolescents and adults","administration_time":"3-5 minutes","norms":{"adults":{"mean":22.6,"sd":4.7,"reference":"Schmitt & Allik, 2005"}}}}'::jsonb
);

